--
-- Table structure for table `sw_webgame`
--

DROP TABLE IF EXISTS `sw_webgame`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!40101 SET character_set_client = utf8 */;
CREATE TABLE `sw_webgame` (
  `id` smallint(5) unsigned NOT NULL AUTO_INCREMENT COMMENT '游戏ID',
  `code` char(10) NOT NULL COMMENT '游戏代码',
  `name` varchar(50) NOT NULL COMMENT '游戏名称',
  `description` varchar(500) NOT NULL COMMENT '游戏描述',
  `brief` varchar(200) DEFAULT NULL COMMENT '游戏简介',
  `pic_small` varchar(200) NOT NULL COMMENT '游戏小图',
  `pic_middle` varchar(200) NOT NULL COMMENT '游戏中图',
  `pic_big` varchar(200) NOT NULL COMMENT '游戏大图',
  `url_webgame` varchar(100) NOT NULL DEFAULT '',
  `url_home` varchar(100) NOT NULL COMMENT '游戏官网地址',
  `url_server` varchar(100) NOT NULL COMMENT '游戏服务器列表地址',
  `url_bbs` varchar(120) NOT NULL COMMENT 'BBS地址',
  `coin_name` char(15) NOT NULL COMMENT '游戏币名称',
  `coin_unit` char(10) NOT NULL COMMENT '游戏币单位',
  `coin_rate` smallint(5) unsigned NOT NULL COMMENT '游戏币兑换比率',
  `listorder` smallint(5) unsigned NOT NULL COMMENT '游戏排序',
  `ispay` enum('1','0') NOT NULL COMMENT '是否开启游戏充值，1：开启，0：不开启',
  `type` tinyint(4) NOT NULL COMMENT '游戏类型',
  `status` enum('0','1','2','3','4') NOT NULL DEFAULT '1' COMMENT '游戏状态',
  `url_maintain` varchar(200) DEFAULT '1' COMMENT '维护跳转地址',
  `start_maintain` int(10) NOT NULL COMMENT '维护开始时间',
  `end_maintain` int(10) NOT NULL COMMENT '维护结束时间',
  PRIMARY KEY (`id`),
